/*List of Functions; Run the following command
 * 
log.info testRunner.metaClass.methods*.name.unique().sort()

*/

class Hello {

	def log;
	def context;
	def testRunner;

	def Hello(log,context,testRunner)
	{
		this.log = log;
		this.context = context; 
		this.testRunner = testRunner;
	}

	def sayHello(String name)
	{
		log.info("  Hello  "+name);

	}

}

context.setProperty("Hello", new Hello(log, context, testRunner))

testRunner.testCase.testSuite.project.getTestSuiteByName('TestSuite3').getTestCaseByName('TestCase1').setSetupScript('log.info "setup for Test1"')
testRunner.testCase.testSuite.project.getTestSuiteByName('TestSuite3').getTestCaseByName('TestCase1').setTearDownScript('log.info "teardown for Test"')

log.info (" >> "+testRunner.testCase.getSetupScript());
log.info (" >> "+testRunner.testCase.getTearDownScript());

project = testRunner.testCase.testSuite.project;
tcase = project.testSuites["TestSuite3"].testCases["TestCase1"];
hello = tcase.getTestStepByName("Hello");


hello.run(testRunner, context);

context.Hello.sayHello("Sumant");

